import React, { Component } from "react";
import { MDBContainer, MDBRow, MDBCol } from "mdbreact";
import DocsLink from "./../components/docsLink";
import comsoc from "./comsoc.jpg";

class FormsPage extends Component {
  constructor(){
    super()
    this.state = {
      searchValue: '',
      isPersonSelected: false,
      modal: false,
      First_Name: '',
      Last_Name:'',
      Sex: '',
      RegNumber:'',
      Program:'',
      Position:'',
      People: loadPeople() || [],
      Person: {},

    }
submitHandler = event => {
    event.preventDefault();
    event.target.className += " was-validated";
  };

  changeHandler = event => {
    this.setState({ ...this.state, [event.target.First_Name]: event.target.value });
  };
  //local storage code
  const loadPeople = () => {
  return JSON.parse(localStorage.getPerson('people'))
} 

const savePerson = (people) => {
  return new Promise((resolve, reject) => {
    localStorage.setPerson('people', JSON.stringify(people))
    resolve(loadPeople())
  })
} 

    this.renderPerson = this.renderPerson.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.handleFormSubmit = this.handleFormSubmit.bind(this)
    this.toggle = this.toggle.bind(this);
    this.onChange = this.onChange.bind(this)
    this.removePerson = this.removePerson.bind(this)
    this.filterOut = this.filterOut.bind(this)
    this.setSelectedPerson = this.setSelectedPerson.bind(this)
  }

  renderPerson(){

    return this.state.people.map((person)=>{
     return <Person 
        person={person}
        selectedPerson={this.setSelectedPerson}/>
    })

  }

  handleFormSubmit(e){
    console.log(this.state.First_Name, this.state.Last_Name)

    const { First_Name, Last_Name } = this.state; 
    const newpeople = [
      ...this.state.people, 
      {id: this.generateID(5), First_Name, Last_Name}
    ]

    savePerson(newpeople)
      .then((result) => {
        return this.setState({
          people: result
        })

      })
    e.preventDefault()
    this.toggle()
  }

  generateID(length){
    let result           = '';
    const characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    for ( var i = 0; i < length; i++ ) {
       result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  }

  onChange(e){
    this.setState({[e.target.First_Name]: e.target.value})
  }

  handleChange(event) {
    this.setState({searchValue: event.target.value});
    this.people = this.search(event.target.value)
  }

  search(First_Name){
    return this.state.people.filter((person) => {
      return person.First_Name.includes(First_Name)
    })
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  removePerson = (First_Name) => {
    const newpeople = this.filterOut(First_Name)

    savePerson(newpeople)
      .then((result) => {

        return this.setState({
          people: result,
          person: {},
          isPersonSelected: false
        })

      })
  }

  filterOut(First_Name) {
    return this.state.people.filter((person) => {
      return !person.First_Name.includes(First_Name)
    })
  }

  setSelectedPerson(person){
    this.setState({person, isPersonSelected: true})
  }
  //local storage code ends here
render() {

     return (
      <MDBContainer>

        <DocsLink
         title="REGISTRATION " 

         />
       <h2 className="mb-5"></h2>
       <MDBRow className="mt-6">
          <MDBCol md="">
          <div className="text-center"><img
              alt="comsoc"

              src={comsoc}
              style={{ height: '8rem', width: '8.5rem', top:'10rem'  }}
            /></div>
            <br></br>
           <form id="formData"
              className="needs-validation"
              onSubmit={this.submitHandler}
              noValidate
            >
              <MDBRow>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterNameEx"
                    className="grey-text"
                  >
                    First First_Name
                  </label>
                  <input

                     value={this.state.First_Name}
                    First_Name="First_Name"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterNameEx"
                    className="form-control"
                    placeholder="First First_Name"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterEmailEx2"
                    className="grey-text"
                  >
                    Last First_Name
                  </label>
                  <input
                    value={this.state.Last_Name}
                    First_Name="Last_Name"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterEmailEx2"
                    className="form-control"
                    placeholder="Last First_Name"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterNameEx"
                    className="grey-text"
                  >
                    Sex
                  </label>
                  <input
                    value={this.state.Sex}
                    First_Name="Sex"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterNameEx"
                    className="form-control"
                    placeholder="Sex"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
              </MDBRow>
              <MDBRow>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text"
                  >
                    Registration Number
                  </label>
                  <input
                    value={this.state.RegNumber}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterPasswordEx4"
                    className="form-control"
                    First_Name="RegNumber"
                    placeholder="Reg. Number"
                    required
                  />
                  <div style={{ top: "auto" }} className="invalid-tooltip">
                    Please provide a valid Reg. Number.
                  </div>
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text"
                  >
                    Program
                  </label>
                  <input
                    value={this.state.Program}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterPasswordEx4"
                    className="form-control"
                    First_Name="program"
                    placeholder="Program of study"
                    required
                  />
                  <div style={{ top: "auto" }} className="invalid-tooltip">
                    Please provide a valid Program of study.
                  </div>
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text"
                  >
                    Position
                  </label>
                  <input
                    value={this.state.Position}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterPasswordEx4"
                    className="form-control"
                    First_Name="Position"
                    placeholder="Position in COMSOC"
                    required
                  />
                  <div style={{ top: "auto" }} className="invalid-tooltip">
                    Please provide a valid Position.
                  </div>
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                </MDBRow>
              <br></br>
              <div className="text-center">
              <button
                className="btn btn-small text-white text color blue" type="submit">
               Submit Form
              </button>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
 }

export default FormsPage;
