import React, { Component } from "react";
import { MDBContainer, MDBRow, MDBCol } from "mdbreact";
import DocsLink from "./../components/docsLink";
import comsoc from "./comsoc.jpg";

class FormsPage extends Component {
  constructor(){
    super()
    this.state = {
      searchValue: '',
      isPersonSelected: false,
      modal: false,
      First_Name: '',
      Last_Name:'',
      Sex: '',
      RegNumber:'',
      Program:'',
      Position:'',
      People: loadPeople() || [],
      Person: {},

    }
submitHandler = event => {
    event.preventDefault();
    event.target.className += " was-validated";
  };

  changeHandler = event => {
    this.setState({ ...this.state, [event.target.name]: event.target.value });
  };
  //local storage code
  const loadItems = () => {
  return JSON.parse(localStorage.getItem('items'))
} 

const saveItem = (items) => {
  return new Promise((resolve, reject) => {
    localStorage.setItem('items', JSON.stringify(items))
    resolve(loadItems())
  })
} 

    this.renderItem = this.renderItem.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.handleFormSubmit = this.handleFormSubmit.bind(this)
    this.toggle = this.toggle.bind(this);
    this.onChange = this.onChange.bind(this)
    this.removeItem = this.removeItem.bind(this)
    this.filterOut = this.filterOut.bind(this)
    this.setSelectedItem = this.setSelectedItem.bind(this)
  }

  renderItem(){

    return this.state.items.map((item)=>{
     return <Item 
        item={item}
        selectedItem={this.setSelectedItem}/>
    })

  }

  handleFormSubmit(e){
    console.log(this.state.name, this.state.price)

    const { name, price } = this.state; 
    const newItems = [
      ...this.state.items, 
      {id: this.generateID(5), name, price: `$${price}`}
    ]

    saveItem(newItems)
      .then((result) => {
        return this.setState({
          items: result
        })

      })
    e.preventDefault()
    this.toggle()
  }

  generateID(length){
    let result           = '';
    const characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    for ( var i = 0; i < length; i++ ) {
       result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  }

  onChange(e){
    this.setState({[e.target.name]: e.target.value})
  }

  handleChange(event) {
    this.setState({searchValue: event.target.value});
    this.items = this.search(event.target.value)
  }

  search(name){
    return this.state.items.filter((item) => {
      return item.name.includes(name)
    })
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  removeItem = (name) => {
    const newItems = this.filterOut(name)

    saveItem(newItems)
      .then((result) => {

        return this.setState({
          items: result,
          item: {},
          isItemSelected: false
        })

      })
  }

  filterOut(name) {
    return this.state.items.filter((item) => {
      return !item.name.includes(name)
    })
  }

  setSelectedItem(item){
    this.setState({item, isItemSelected: true})
  }
  //local storage code ends here
render() {

     return (
      <MDBContainer>

        <DocsLink
         title="REGISTRATION " 

         />
       <h2 className="mb-5"></h2>
       <MDBRow className="mt-6">
          <MDBCol md="">
          <div className="text-center"><img
              alt="comsoc"

              src={comsoc}
              style={{ height: '8rem', width: '8.5rem', top:'10rem'  }}
            /></div>
            <br></br>
           <form id="formData"
              className="needs-validation"
              onSubmit={this.submitHandler}
              noValidate
            >
              <MDBRow>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterNameEx"
                    className="grey-text"
                  >
                    First name
                  </label>
                  <input

                     value={this.state.First_Name}
                    name="First_Name"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterNameEx"
                    className="form-control"
                    placeholder="First name"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterEmailEx2"
                    className="grey-text"
                  >
                    Last name
                  </label>
                  <input
                    value={this.state.Last_Name}
                    name="Last_Name"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterEmailEx2"
                    className="form-control"
                    placeholder="Last name"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterNameEx"
                    className="grey-text"
                  >
                    Sex
                  </label>
                  <input
                    value={this.state.Sex}
                    name="Sex"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterNameEx"
                    className="form-control"
                    placeholder="Sex"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
              </MDBRow>
              <MDBRow>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text"
                  >
                    Registration Number
                  </label>
                  <input
                    value={this.state.RegNumber}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterPasswordEx4"
                    className="form-control"
                    name="RegNumber"
                    placeholder="Reg. Number"
                    required
                  />
                  <div style={{ top: "auto" }} className="invalid-tooltip">
                    Please provide a valid Reg. Number.
                  </div>
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text"
                  >
                    Program
                  </label>
                  <input
                    value={this.state.Program}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterPasswordEx4"
                    className="form-control"
                    name="program"
                    placeholder="Program of study"
                    required
                  />
                  <div style={{ top: "auto" }} className="invalid-tooltip">
                    Please provide a valid Program of study.
                  </div>
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text"
                  >
                    Position
                  </label>
                  <input
                    value={this.state.Position}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterPasswordEx4"
                    className="form-control"
                    name="Position"
                    placeholder="Position in COMSOC"
                    required
                  />
                  <div style={{ top: "auto" }} className="invalid-tooltip">
                    Please provide a valid Position.
                  </div>
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                </MDBRow>
              <br></br>
              <div className="text-center">
              <button
                className="btn btn-small text-white text color blue" type="submit">
               Submit Form
              </button>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
 }

export default FormsPage;
