import React, { Component } from "react";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn, MDBCard, MDBCardBody, } from "mdbreact";
import DocsLink from "./../components/docsLink";
import Person from "./components/Person";
import Details from './components/Details';
import ls from 'local-storage'

const loadPeople = () => {
  return JSON.parse(localStorage.getPerson('people'))
} 

const savePerson = (people) => {
  return new Promise((resolve, reject) => {
    localStorage.setPerson('people', JSON.stringify(people))
    resolve(loadPeople())
  })
} 
class FormsPage extends Component {

  constructor(){
     super()
       this.state = {
         searchValue: '',
         isPersonSelected: false,
         modal: false,
         First_Name: '',
         Last_Name: '',
         Sex: '',
         RegNumber: '',
         Program: '',
         Position: '',
         people: loadPeople() || [],
         person: {},


    }
    this.renderPerson = this.renderPerson.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.handleFormSubmit = this.handleFormSubmit.bind(this)
    this.toggle = this.toggle.bind(this)
    this.onChange = this.onChange.bind(this)
    this.removePerson = this.removePerson.bind(this)
    this.filterOut = this.filterOut.bind(this)
    this.setSelectedPerson = this.setSelectedPerson.bind(this)
  }
  renderPerson(){
    return this.state.people.map((person)=>{
      return <Person
      person={person}
      selectedPerson={this.setSelectedPerson}/>
    })
  }

  handleFormSubmit(e){
    console.log(this.state.First_Name, this.Last_Name, this.Sex, this.RegNumber,
                this.Program, this.Position)
    const { First_Name, Last_Name, Sex, RegNumber, Program, Position} = this.state;
    const newPeople = [
      ...this.state.people,
      {id: this.generateID(4),First_Name, Last_Name, Sex, RegNumber, Program, Position}
    ]
    savePerson(newPeople)
     .then((result)=> {
       return this.setState({
         people: result
       })
     })
     e.preventDefault()
     this.toggle()

  }
  generateID(length){
    let result         ='';
    const characters   = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    for ( var i = 0; i < length; i++ ) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength ));
    }
    return result;
  }
  onChange(e){
    this.setState({[e.target.First_Name]: e.target.value})
    this.setState({[e.target.Last_Name]: e.target.value})
    this.setState({[e.target.Sex]: e.target.value})
    this.setState({[e.target.RegNumber]: e.target.value})
    this.setState({[e.target.Program]: e.target.value})
    this.setState({[e.target.Position]: e.target.value})
  }

  handleChange(event) {
    this.setState({searchValue: event.target.value});
    this.person = this.search(event.target.value)
  }

  search(First_Name, Last_Name, Sex, RegNumber, Program, Position){
    return this.state.person.filter((person) => {
      return person.First_Name, person.Last_Name, person.Sex, person.RegNumber, person.Program, person.Position.includes(First_Name, Last_Name, Sex, RegNumber, Program, Position)

    })
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  removePerson = (First_Name, Last_Name, Sex, RegNumber) => {
    const newperson = this.filterOut(First_Name, Last_Name, Sex, RegNumber)

    savePerson(newperson)
      .then((result) => {

        return this.setState({
          person: result,
          person: {},
          isPersonSelected: false
        })

      })
  }

  filterOut(First_Name, Last_Name, Sex, RegNumber) {
    return this.state.person.filter((person) => {
      return !person.First_Name, !person.Last_Name, !person.Sex, !person.RegNumber, !person.Program, !person.Position.includes(First_Name, Last_Name, Sex, RegNumber)
    })
  }

  setSelectedperson(person){
    this.setState({person, isPersonSelected: true})
  }


  render() {

     return (
      <MDBContainer>
        <DocsLink
         title="REGISTRATION FORM" 

         />
        <h2 className="mb-5"></h2>
        <MDBRow className="mt-6">
          <MDBCol md="">
            <form
              className="needs-validation"
              onSubmit={this.submitHandler}
              noValidate

              onSubmit={this.handleSubmit} class="form-inline my-2 my-lg-0">
              <input class="form-control mr-sm-2" type="name" onKeyUp={this.handleChange} placeholder="Search"/>
              <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>

            >
              <MDBRow>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterNameEx"
                    className="grey-text font-weight-bold"
                  >
                    First name
                  </label>
                  <input
                    value={this.state.First_Name}
                    name="First_Name"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterNameEx"
                    className="form-control"
                    placeholder="First name"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                  <br></br>
                </div>
                <div className="col-md-4 mb-3" >
                <label
                    htmlFor="defaultFormRegisterEmailEx2"
                    className="grey-text font-weight-bold"
                  >
                    Last name
                  </label>
                  <input
                    value={this.state.Last_Name}
                    name="Last_Name"
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterEmailEx2"
                    className="form-control"
                    placeholder="Last name"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>

                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterConfirmEx3"
                    className="grey-text font-weight-bold"
                  >
                    Sex
                  </label>
                  <input
                    value={this.state.Sex}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterConfirmEx3"
                    className="form-control"
                    name="Sex"
                    placeholder="Your Sex"
                    required
                  />
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
              </MDBRow>
              <MDBRow>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text font-weight-bold"
                  >
                    Registration Number
                  </label>
                  <input
                    value={this.state.RegNumber}
                    onChange={this.changeHandler}
                    type="text"
                    id="defaultFormRegisterPasswordEx4"
                    className="form-control"
                    name="RegNumber"
                    placeholder="Your Registration number"
                    required
                  />
                  <div style={{ top: "auto" }} className="invalid-tooltip">
                    Please provide a valid Reg.Number.
                  </div>
                  <div style={{ top: "auto" }} className="valid-tooltip">
                    Looks good!
                  </div>
                </div>
                <div className="col-md-4 mb-3">
                  <label
                    htmlFor="defaultFormRegisterPasswordEx4"
                    className="grey-text font-weight-bold"
                  >
                    Program
                  </label>
                  <input
                    value={this.state.Program}
                    onChange={this.changeHandler}
                    type="text"
